import { useState } from 'react';

export const useMenuLogic = () => {
  const [isMenuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setMenuOpen(false);
  };

  return { isMenuOpen, toggleMenu, closeMenu };
};
