import React, { useState, useRef, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Switch, Link } from 'react-router-dom';
import { useMenuLogic } from './SlideOutMenu';
import { submitComment } from './front-back-end-link';
import { Header, HeroSection, CardContainer, SlideOutMenu } from './Components';
import CommentFormPage from './CommentFormPage';
import useOutsideClick from './useOutsideClick';
import './App.css';
import './animations.css';
import './SlideOutMenu';

// Main app structure component that holds all the other components in Components.js
function App() {
  const [showContent, setShowContent] = useState(true);
  const menuRef = useRef();
  const { isMenuOpen, toggleMenu, closeMenu, handleMenuClick } = useMenuLogic();

  // Function called when 'Explore' link is clicked
  const handleCommentClick = () => {
    setShowContent(false);
  };

  // Function called when user clicks outside menu
  useOutsideClick(menuRef, () => {
    if (isMenuOpen) {
      closeMenu();
    }
  });

  return (
    <Router>
      <div className="app">
        <Header toggleMenu={toggleMenu} />
        {showContent ? (
          <HeroSection showContent={showContent} onCommentClick={handleCommentClick} />
        ) : (
          <CommentFormPage/>

        )}
        <CardContainer />
        <SlideOutMenu ref={menuRef} isMenuOpen={isMenuOpen} /> 
      </div>
    </Router>
  );
}

export default App;
