import React, { useEffect, useRef } from 'react';
import userProfileLogo from './images/user-profile-logo.png';
import './SlideOutMenu.css';

const SlideOutMenu = ({ isMenuOpen, toggleMenu }) => {
  const menuRef = useRef(null);

  // Click outside handler
  useEffect(() => {
    function handleClickOutside(event) {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        if (isMenuOpen) {
          toggleMenu();
        }
      }
    }
    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isMenuOpen, toggleMenu]); // Add toggleMenu to the dependency array

  // Slide out menu structure
  return (
    <div ref={menuRef} className={`slide-out-menu ${isMenuOpen ? 'open' : ''}`}>
      <div style={{ backgroundImage: `url(${userProfileLogo})` }} className="menu__photo"></div>
      {/* ... rest of your menu items */}
    </div>
  );
};

export default SlideOutMenu;
