import React, { useEffect, useRef } from 'react';
import './SlideOutMenu.css';

const SlideOutMenu = ({ isMenuOpen, toggleMenu }) => {
  const menuRef = useRef(null); // Ref

  useEffect(() => {
    function handleClickOutside(event) {
      console.log('Document was clicked'); // Debugging line to ensure clicks are detected

      if (menuRef.current && !menuRef.current.contains(event.target)) {
        console.log('Click was outside menuRef'); // Debugging line to check if the click is outside
        if (isMenuOpen) {
          console.log('Menu is open, should close'); // Debugging line to check if the menu is open
          toggleMenu();
        }
      }
    }

    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isMenuOpen, toggleMenu]); // Ensuring re-subscription when props change

  return (
    <div ref={menuRef} className={`slide-out-menu ${isMenuOpen ? 'open' : ''}`}>
      <div className="menu__photo"></div>
    </div>
  );
};

export default SlideOutMenu;
